package com.hire.foxes.bottom_navigation;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.widget.ImageView;
import android.widget.TextView;

import com.hire.foxes.R;
import com.hire.foxes.bottom_navigation.adapters.home_services_adapter;
import com.hire.foxes.bottom_navigation.adapters.provider_adapter;
import com.hire.foxes.bottom_navigation.pojo_class.home_services_data;
import com.hire.foxes.bottom_navigation.pojo_class.provider_data;

import java.util.ArrayList;
import java.util.List;

public class provider extends AppCompatActivity {

    RecyclerView provider_recyclerView;
    List<provider_data> providers_list;
    ImageView provider_back_button,provider_searchView;
    TextView provider_service_name;
    String service_name;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_provider);

        //TextView
        provider_service_name = findViewById(R.id.provider_service_name);
        //ImageView
        provider_back_button = findViewById(R.id.provider_back_button);
        provider_searchView = findViewById(R.id.provider_searchView);
        //RecyclerView
        provider_recyclerView = findViewById(R.id.provider_recyclerView);
        //Intent
        service_name = getIntent().getStringExtra("service_name");
        provider_service_name.setText(service_name);
        //List
        providers_list = new ArrayList<>();

        providers_list.add(new provider_data("Aditya","data:image/png;base64,"));
        providers_list.add(new provider_data("Arjun","data:image/png;base64,"));
        providers_list.add(new provider_data("Ryan","data:image/png;base64,"));
        providers_list.add(new provider_data("Kabir","data:image/png;base64,"));
        providers_list.add(new provider_data("Aarush","data:image/png;base64,"));
        providers_list.add(new provider_data("Dev","data:image/png;base64,"));
        providers_list.add(new provider_data("Jai","data:image/png;base64,"));
        providers_list.add(new provider_data("Samar","data:image/png;base64,"));
        providers_list.add(new provider_data("Viaan","data:image/png;base64,"));
        providers_list.add(new provider_data("Devansh","data:image/png;base64,"));
        providers_list.add(new provider_data("Yash","data:image/png;base64,"));



        provider_adapter adapter = new provider_adapter(provider.this,providers_list);
        provider_recyclerView.setAdapter(adapter);

    }
}